Gamma distribution

## function for fitting
fit_gamma <-
function(q, p = 0.95) {
  p <- c(0, p) + (1 - p)/2

  f <-
    function(x, p, target) {
      dev <- qgamma(p = p, shape = x[1], rate = x[2])
      return(sum((dev - target) ^ 2))
    }

  fit <- optim(par = c(1, 1), fn = f, p = p, target = q)

  return(fit$par)
}

