## create SIR function
sir &lt;- function(time, state, parameters) {

  with(as.list(c(state, parameters)), {

    dS &lt;- -beta * S * I
    dI &lt;-  beta * S * I - alpha * I
    dR &lt;-                 alpha * I

    return(list(c(dS, dI, dR)))
  })
}
